<!--
	@component
	A wrapped textarea component integrated with Carta. It handles the highlighting
	and propagates events to the Carta instance.	
-->

<script>import { onMount } from "svelte";
import { browser } from "$app/environment";
import { debounce } from "../utils";
import { isSingleTheme, loadNestedLanguages } from "../highlight";
export let carta;
export let value = "";
export let placeholder = "";
export let elem;
export let props = {};
let textarea;
let highlighElem;
let highlighted = value;
let mounted = false;
export const resize = () => {
  if (!mounted || !textarea) return;
  textarea.style.height = highlighElem.scrollHeight + "px";
  textarea.scrollTop = 0;
};
const focus = () => {
  const selectedText = window.getSelection()?.toString();
  if (selectedText) return;
  textarea?.focus();
};
const highlight = async (text) => {
  const highlighter = await carta.highlighter();
  let html;
  if (isSingleTheme(highlighter.theme)) {
    html = highlighter.codeToHtml(text, {
      lang: highlighter.lang,
      theme: highlighter.theme
    });
  } else {
    html = highlighter.codeToHtml(text, {
      lang: highlighter.lang,
      themes: highlighter.theme
    });
  }
  if (carta.sanitizer) {
    highlighted = carta.sanitizer(html);
  } else {
    highlighted = html;
  }
};
const highlightNestedLanguages = debounce(async (text) => {
  const highlighter = await carta.highlighter();
  const { updated } = await loadNestedLanguages(highlighter, text);
  if (updated) highlight(text);
}, 300);
const onValueChange = (value2) => {
  highlight(value2).then(resize);
  highlightNestedLanguages(value2);
};
$: if (browser) onValueChange(value);
onMount(() => {
  mounted = true;
  requestAnimationFrame(resize);
});
onMount(() => {
  carta.$setInput(textarea, elem, () => {
    value = textarea.value;
    highlight(value);
  });
});
</script>

<div role="tooltip" id="editor-unfocus-suggestion">
	Press ESC then TAB to move the focus off the field
</div>
<div
	on:click={focus}
	on:keydown={focus}
	on:scroll
	role="textbox"
	tabindex="-1"
	class="carta-input"
	bind:this={elem}
>
	<div class="carta-input-wrapper">
		<div
			class="carta-highlight carta-font-code"
			tabindex="-1"
			aria-hidden="true"
			bind:this={highlighElem}
		>
			<!-- eslint-disable-line svelte/no-at-html-tags -->{@html highlighted}
		</div>

		<textarea
			name="md"
			id="md"
			spellcheck="false"
			class="carta-font-code"
			aria-multiline="true"
			aria-describedby="editor-unfocus-suggestion"
			tabindex="0"
			{placeholder}
			{...props}
			bind:value
			bind:this={textarea}
			on:scroll={() => (textarea.scrollTop = 0)}
		/>
	</div>

	{#if mounted}
		<slot />
	{/if}
</div>

<style>
	.carta-input {
		position: relative;
	}

	.carta-input-wrapper {
		height: 100%;
		position: relative;
		font-family: monospace;
	}

	textarea {
		position: relative;
		width: 100%;
		max-width: 100%;
		min-height: 100%;

		overflow-y: hidden;
		resize: none;

		padding: 0;
		margin: 0;
		border: 0;

		color: transparent;
		background: transparent;

		outline: none;
		tab-size: 4;
	}

	.carta-highlight {
		position: absolute;
		left: 0;
		right: 0;
		top: 0;
		bottom: 0;
		margin: 0;
		user-select: none;
		height: fit-content;

		padding: inherit;
		margin: inherit;

		word-wrap: break-word;
		white-space: pre-wrap;
		word-break: break-word;
	}

	:global(.carta-highlight .shiki) {
		margin: 0;
		tab-size: 4;
		background-color: transparent !important;
	}

	:global(.carta-highlight *) {
		font-family: inherit;
		font-size: inherit;

		word-wrap: break-word;
		white-space: pre-wrap;
		word-break: break-word;
	}

	#editor-unfocus-suggestion {
		position: absolute;
		width: 1px;
		height: 1px;
		padding: 0;
		margin: -1px;
		overflow: hidden;
		clip: rect(0, 0, 0, 0);
		border: 0;
	}
</style>
